  /* Calculate geometric coordinates of Moon
   * without light time or nutation correction.
   */
  int swi_moshmoon2(double J, double[] pol) {
    int i;
    T = (J-SwephData.J2000)/36525.0;
    T2 = T*T;
    mean_elements();
    mean_elements_pl();
    moon1();
    moon2();
    moon3();
    moon4();
    for( i=0; i<3; i++ )
      pol[i] = moonpol[i];
    return(0);
  }

